import React, { Component } from "react";

class MainContent extends Component {
  state = {
    pageTitle: "Customers",
    customersCount: 5,
    customers: [
      {
        id: 1,
        name: "Scott",
        phone: "123-456",
        address: { city: "New York" },
        photo: "https://picsum.photo/id/237/300/300",
      },
      { id: 2, name: "Alex", phone: "123-111", address: { city: "Chicago" } },
      {
        id: 3,
        name: "Smith",
        phone: "516-410",
        address: { city: "Indianpolis" },
      },
      { id: 4, name: "Rob", phone: null, address: { city: "Las Vegas" } },
      {
        id: 5,
        name: "Billy",
        phone: "917-676",
        address: { city: "New Orleans" },
      },
    ],
  };

  render() {
    return (
      <div>
        <h4 className="border-bottom m-1 p-1">
          {this.state.pageTitle}
          <span className="badge bg-secondary m-2">
            {this.state.customersCount}
          </span>

          <button className="btn btn-info" onClick={this.onRefreshClick}>
            Easy Button
          </button>
        </h4>
        <table className="table">
          <thead>
            <tr>
              <th>ID #</th>
              <th>Customer Name</th>
              <th>Phone Number</th>
              <th>City</th>
            </tr>
          </thead>
          <tbody>{this.getCustomerRow()}</tbody>
        </table>
      </div>
    );
  }

  // executes when the user clicks on Refresh button
  onRefreshClick = () => {
    this.setState({
      customersCount: 32,
    });
  };

  getPhoneToRender = (customerInput) => {
    return customerInput.phone == null ? (
      <div className="nullCT"> Not listed </div>
    ) : (
      customerInput.phone
    );
  };

  getCustomerRow = () => {
    return this.state.customers.map((customerInput) => {
      return (
        <tr key={customerInput.id}>
          <td>{customerInput.id}</td>
          <td>{customerInput.name}</td>
          <td>{this.getPhoneToRender(customerInput)}</td>
          <td>{customerInput.address.city}</td>
        </tr>
      );
    });
  };
}

export default MainContent;
